// src/App.js
import React, { useState, useRef } from 'react';
import axios from 'axios';
import './App.css'; // Ensure you have Tailwind CSS imported here

const API_BASE_URL = 'http://127.0.0.1:5000';

const App = () => {
  const [activeTab, setActiveTab] = useState('youtube'); // 'youtube' or 'upload'
  const [url, setUrl] = useState('');
  const [query, setQuery] = useState('');
  const [message, setMessage] = useState('');
  const [results, setResults] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [isQuerying, setIsQuerying] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentClip, setCurrentClip] = useState(null);
  const [filename, setFilename] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const videoRef = useRef(null);

  const handleTabSwitch = (tab) => {
    setActiveTab(tab);
    setMessage('');
    setResults([]);
    setCurrentClip(null);
  };

  const handleProcess = async () => {
    if (!url.trim()) {
      setMessage('Please enter a YouTube URL.');
      return;
    }

    setIsProcessing(true);
    setMessage('');
    try {
      const response = await axios.post(`${API_BASE_URL}/process`, { url });
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setMessage(response.data.message);
        setFilename(response.data.filename);
      }
    } catch (error) {
      setMessage('Error processing video.');
    }
    setIsProcessing(false);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setMessage('Please select a video file to upload.');
      return;
    }

    setIsUploading(true);
    setMessage('');
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setMessage(response.data.message);
        setFilename(response.data.filename);
      }
    } catch (error) {
      setMessage('Error uploading video.');
    }
    setIsUploading(false);
  };

  const handleQuery = async () => {
    if (!query.trim()) {
      setMessage('Please enter a query.');
      return;
    }

    setIsQuerying(true);
    setResults([]);
    setMessage('');
    try {
      const response = await axios.post(`${API_BASE_URL}/query`, { query });
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setResults(response.data);
      }
    } catch (error) {
      setMessage('Error querying video.');
    }
    setIsQuerying(false);
  };

  const playClip = async (startTime, endTime) => {
    if (!filename) {
      setMessage('No video file available for clipping.');
      return;
    }

    setMessage('');
    const clipData = { filename, start_time: startTime, end_time: endTime };
    try {
      const response = await axios.post(`${API_BASE_URL}/clip`, clipData);
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setCurrentClip(response.data.clip_url);
      }
    } catch (error) {
      setMessage('Error creating clip.');
    }
  };

  const handleDeleteIndex = async () => {
    setIsDeleting(true);
    setMessage('');
    try {
      const response = await axios.post(`${API_BASE_URL}/delete-index`);
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setMessage(response.data.message);
      }
    } catch (error) {
      setMessage('Error deleting index.');
    }
    setIsDeleting(false);
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-3xl">
        {/* Title */}
        <h1 className="text-4xl font-sawarabi text-center mb-6 text-pink-600">
          Video Query
        </h1>

        {/* Feedback Message */}
        {message && (
          <div className="mb-4 p-4 bg-yellow-100 border-l-4 border-yellow-500 text-gray-700">
            {message}
          </div>
        )}

        {/* Tabs for YouTube and Upload */}
        <div className="flex justify-center mb-6">
          <button
            onClick={() => handleTabSwitch('youtube')}
            className={`px-4 py-2 mr-2 rounded-t-lg focus:outline-none ${
              activeTab === 'youtube'
                ? 'bg-pink-500 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            YouTube URL
          </button>
          <button
            onClick={() => handleTabSwitch('upload')}
            className={`px-4 py-2 rounded-t-lg focus:outline-none ${
              activeTab === 'upload'
                ? 'bg-pink-500 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Upload Video
          </button>
        </div>

        {/* Active Tab Content */}
        {activeTab === 'youtube' && (
          <div className="space-y-4">
            {/* YouTube URL Processing */}
            <div className="bg-gray-50 p-4 rounded-lg">
              <input
                type="url"
                placeholder="Enter YouTube URL"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded mb-3 focus:outline-none focus:ring-2 focus:ring-pink-500"
              />
              <button
                onClick={handleProcess}
                disabled={isProcessing}
                className={`w-full bg-pink-500 text-white py-2 rounded hover:bg-pink-600 transition duration-200 ${
                  isProcessing ? 'opacity-50 cursor-not-allowed' : ''
                }`}
              >
                {isProcessing ? 'Processing...' : 'Process Video'}
              </button>
            </div>
          </div>
        )}

        {activeTab === 'upload' && (
          <div className="space-y-4">
            {/* Local Video Upload */}
            <div className="bg-gray-50 p-4 rounded-lg">
              <input
                type="file"
                accept="video/*"
                onChange={handleFileChange}
                className="w-full mb-3"
              />
              <button
                onClick={handleUpload}
                disabled={isUploading || !selectedFile}
                className={`w-full bg-pink-500 text-white py-2 rounded hover:bg-pink-600 transition duration-200 ${
                  isUploading || !selectedFile ? 'opacity-50 cursor-not-allowed' : ''
                }`}
              >
                {isUploading ? 'Uploading...' : 'Upload Video'}
              </button>
            </div>
          </div>
        )}

        {/* Delete Index Button */}
        <div className="text-center mt-4">
          <button
            onClick={handleDeleteIndex}
            disabled={isDeleting}
            className={`bg-pink-500 text-white py-2 px-4 rounded hover:bg-pink-600 transition duration-200 ${
              isDeleting ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {isDeleting ? 'Deleting...' : 'Delete Index'}
          </button>
        </div>

        {/* Query Section */}
        <div className="mt-8 bg-gray-50 p-4 rounded-lg">
          <h2 className="text-2xl font-medium mb-2">Query Video</h2>
          <input
            type="text"
            placeholder="Enter your query"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded mb-3 focus:outline-none focus:ring-2 focus:ring-pink-500"
          />
          <button
            onClick={handleQuery}
            disabled={isQuerying}
            className={`w-full bg-pink-500 text-white py-2 rounded hover:bg-pink-600 transition duration-200 ${
              isQuerying ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {isQuerying ? 'Querying...' : 'Query'}
          </button>
        </div>

        {/* Results Section */}
        {results.length > 0 && (
          <div className="mt-8 bg-gray-50 p-4 rounded-lg">
            <h2 className="text-2xl font-medium mb-4">Results</h2>
            {results.map((result, index) => (
              <div key={index} className="mb-4 p-4 bg-white rounded shadow">
                <p><strong>Time:</strong> {result.start_time} - {result.end_time}</p>
                <p><strong>Description:</strong> {result.description}</p>
                <p><strong>Score:</strong> {result.score.toFixed(4)}</p>
                <button
                  onClick={() => playClip(result.start_time, result.end_time)}
                  className="mt-2 bg-pink-500 text-white py-1 px-3 rounded hover:bg-pink-600 transition duration-200"
                >
                  Play Clip
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Current Clip Section */}
        {currentClip && (
          <div className="mt-8 bg-gray-50 p-4 rounded-lg">
            <h2 className="text-2xl font-medium mb-4">Current Clip</h2>
            <video
              ref={videoRef}
              controls
              className="w-full rounded"
              src={`${API_BASE_URL}${currentClip}`}
            >
              Your browser does not support the video tag.
            </video>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
