import React, { useState, useRef } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://127.0.0.1:5000';

const App = () => {
  const [url, setUrl] = useState('');
  const [query, setQuery] = useState('');
  const [message, setMessage] = useState('');
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentClip, setCurrentClip] = useState(null);
  const [filename, setFilename] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const videoRef = useRef(null);

  const handleProcess = async () => {
    if (!url.trim()) {
      setMessage('Please enter a YouTube URL.');
      return;
    }

    setIsLoading(true);
    setMessage('');
    try {
      const response = await axios.post(`${API_BASE_URL}/process`, { url });
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setMessage(response.data.message);
        setFilename(response.data.filename);
      }
    } catch (error) {
      setMessage('Error processing video.');
    }
    setIsLoading(false);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setMessage('Please select a video file to upload.');
      return;
    }

    setIsLoading(true);
    setMessage('');
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setMessage(response.data.message);
        setFilename(response.data.filename);
      }
    } catch (error) {
      setMessage('Error uploading video.');
    }
    setIsLoading(false);
  };

  const handleQuery = async () => {
    if (!query.trim()) {
      setMessage('Please enter a query.');
      return;
    }

    setIsLoading(true);
    setResults([]);
    setMessage('');
    try {
      const response = await axios.post(`${API_BASE_URL}/query`, { query });
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setResults(response.data);
      }
    } catch (error) {
      setMessage('Error querying video.');
    }
    setIsLoading(false);
  };

  const playClip = async (startTime, endTime) => {
    if (!filename) {
      setMessage('No video file available for clipping.');
      return;
    }

    setIsLoading(true);
    setMessage('');
    const clipData = { filename, start_time: startTime, end_time: endTime };
    try {
      const response = await axios.post(`${API_BASE_URL}/clip`, clipData);
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setCurrentClip(response.data.clip_url);
      }
    } catch (error) {
      setMessage('Error creating clip.');
    }
    setIsLoading(false);
  };

  const handleDeleteIndex = async () => {
    setIsLoading(true);
    setMessage('');
    try {
      const response = await axios.post(`${API_BASE_URL}/delete-index`);
      if (response.data.error) {
        setMessage(response.data.error);
      } else {
        setMessage(response.data.message);
      }
    } catch (error) {
      setMessage('Error deleting index.');
    }
    setIsLoading(false);
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-3xl">
        <h1 className="text-3xl font-semibold text-center mb-6">Video Processing & Querying</h1>

        {/* Feedback Message */}
        {message && (
          <div className="mb-4 p-4 bg-yellow-100 border-l-4 border-yellow-500 text-gray-700">
            {message}
          </div>
        )}

        {/* Processing and Uploading Section */}
        <div className="space-y-6">
          {/* YouTube URL Processing */}
          <div className="bg-gray-50 p-4 rounded-lg">
            <h2 className="text-xl font-medium mb-2">Process YouTube Video</h2>
            <input
              type="url"
              placeholder="Enter YouTube URL"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              onClick={handleProcess}
              disabled={isLoading}
              className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 disabled:bg-blue-300 transition duration-200"
            >
              {isLoading ? 'Processing...' : 'Process Video'}
            </button>
          </div>

          {/* Local Video Upload */}
          <div className="bg-gray-50 p-4 rounded-lg">
            <h2 className="text-xl font-medium mb-2">Upload Local Video</h2>
            <input
              type="file"
              accept="video/*"
              onChange={handleFileChange}
              className="w-full mb-3"
            />
            <button
              onClick={handleUpload}
              disabled={isLoading || !selectedFile}
              className="w-full bg-indigo-500 text-white py-2 rounded hover:bg-indigo-600 disabled:bg-indigo-300 transition duration-200"
            >
              {isLoading ? 'Uploading...' : 'Upload Video'}
            </button>
          </div>

          {/* Delete Index Button */}
          <div className="text-center">
            <button
              onClick={handleDeleteIndex}
              disabled={isLoading}
              className="bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 disabled:bg-red-300 transition duration-200"
            >
              {isLoading ? 'Deleting...' : 'Delete Index'}
            </button>
          </div>
        </div>

        {/* Query Section */}
        <div className="mt-8 bg-gray-50 p-4 rounded-lg">
          <h2 className="text-xl font-medium mb-2">Query Video</h2>
          <input
            type="text"
            placeholder="Enter your query"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded mb-3 focus:outline-none focus:ring-2 focus:ring-green-500"
          />
          <button
            onClick={handleQuery}
            disabled={isLoading}
            className="w-full bg-green-500 text-white py-2 rounded hover:bg-green-600 disabled:bg-green-300 transition duration-200"
          >
            {isLoading ? 'Querying...' : 'Query'}
          </button>
        </div>

        {/* Results Section */}
        {results.length > 0 && (
          <div className="mt-8 bg-gray-50 p-4 rounded-lg">
            <h2 className="text-xl font-medium mb-4">Results</h2>
            {results.map((result, index) => (
              <div key={index} className="mb-4 p-4 bg-white rounded shadow">
                <p><strong>Time:</strong> {result.start_time} - {result.end_time}</p>
                <p><strong>Description:</strong> {result.description}</p>
                <p><strong>Score:</strong> {result.score.toFixed(4)}</p>
                <button
                  onClick={() => playClip(result.start_time, result.end_time)}
                  className="mt-2 bg-purple-500 text-white py-1 px-3 rounded hover:bg-purple-600 transition duration-200"
                >
                  Play Clip
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Current Clip Section */}
        {currentClip && (
          <div className="mt-8 bg-gray-50 p-4 rounded-lg">
            <h2 className="text-xl font-medium mb-4">Current Clip</h2>
            <video
              ref={videoRef}
              controls
              className="w-full rounded"
              src={`${API_BASE_URL}${currentClip}`}
            >
              Your browser does not support the video tag.
            </video>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
